import logo from './logo.svg';
import './App.css';
import Recipe from './Recipes';
import React,{ Component, useEffect, useState } from 'react';

const App = () =>{
  const APP_ID = "334c5b9f";
  const APP_KEY = "3c8b9486e1e3276400218a77896c18e9";
  const url ="https://api.edamam/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}";

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('mango');
  useEffect(() => {
   getRecipes();
  },[query]);
  const getRecipes = async () => {
    const response = await fetch('https://api.edamam.com/search?q='+query+'&app_id=334c5b9f&app_key=3c8b9486e1e3276400218a77896c18e9');
    const data = await response.json();
    setRecipes(data.hits);
  console.log(data.hits);
  };

  const updateSearch = e =>{
    setSearch(e.target.value);
    console.log(e.target.value)
  }
  const getSearch = e =>{
   e.preventDefault();
  setQuery(search);
  }

  return(
    <div className='App'>
     <form onSubmit={getSearch} className='search-form'>
      <input onChange={updateSearch} value={search} className='search-bar' type="text" placeholder="Search"/>
      <button className='search-button' type='submit'> Search
      </button>
     </form>
     {recipes.map(recipe => (
      <Recipe 
      key ={recipe.recipe.label}
      title ={recipe.recipe.label}
      image = {recipe.recipe.image}
       />
     ))}

    </div>
  );
}
export default App;
