import React from "react";

const Recipe = ({ title, calories, image, ingredients,ingredientLines, source }) => {
  return (


    <li class="cards_item">
    <div class="card">
      <div class="card_image"><img src={image} alt="mixed vegetable salad in a mason jar. "/></div>
      <div class="card_content">
        <h2 class="card_title">{title}</h2>
        <h2 class="card_title">{calories.toFixed(0)} Calories</h2>
        <div class="card_text">
        {ingredients.map((ingredient) => (
        <p> {ingredient}</p>
      ))}
          {/* <p>Dig into the freshest veggies of the season! This salad-in-a-jar features a mixture of leafy greens and seasonal vegetables, fresh from the farmer's market.</p> */}
          <p> {source}</p>
        </div>
      </div>
    </div>
  </li>
  );
};

export default Recipe;
