import React from "react";
import { Link } from 'react-router-dom'



const Recipe = ({
  title,
  calories,
  image,
  ingredients,
  ingredientLines,
  source,
  recipe
}) => {
  // console.log(recipe);
  return (
      <li class="cards_item">
      <div class="card">
        <div class="card_image">
        <Link to='/details' state={recipe}>

        {/* <Link to={{ pathname: '/details', state = {'aaa': 123} }}> */}
          <img src={image} alt="" />
          </Link>
        </div>
        <div class="card_content">

          <h2 class="card_title">{title}</h2>
          <div class="card_text">
            <p>{calories.toFixed(0)} calories</p>
            <p> {ingredients.length} ingredients</p>
            </div>
        </div>
      </div>
    </li>




  );
};

export default Recipe;
